#-----------------------------------------------------------------------------------------------------------------------

def _call(mols, fn, *args, **kwargs):
    return [getattr(m, fn)(*args, **kwargs) for m in mols if m and hasattr(m, fn) ]

#-----------------------------------------------------------------------------------------------------------------------

def _apply(mols, fn, *args, **kwargs):
    return [fn(m, *args, **kwargs) for m in mols if m ]

